Spot.create!([
  {name: "知床 （北海道）", content: "北海道東部に位置する知床半島は、世界で最も低緯度で流氷が接岸する場所。オホーツク海の豊かな海の恵みを得て、プランクトン、魚介類、アザラシ、陸上ではキツネやヒグマ、オオワシなどが集まり、一大食物連鎖が繰り広げられる場所です。知床岳や知床五湖など、人の手が及ばない、生まれたままの姿を残す大自然が見ものです。", image: "https://d1f5hsy4d47upe.cloudfront.net/38/383ebc1660710f56c1f2a5e4e5f6b708_t.jpeg", region: "北海道", address: "知床", status: nil, latitude: 44.2005, longitude: 145.24},
  {name: "白神山地 （青森県・秋田県）", content: "青森県と秋田県にまたがる13万ヘクタールに及ぶ白神山地。人為の影響をほとんど受けていない原生的なブナの天然林が広がり、その規模は東アジア最大級。多種多様な植物が生育し、多くの動物が生息する豊かな森が広がっています。30分から1時間程度で楽しめる散策コースから本格的な登山コースまで揃い、四季折々の世界遺産の景観を楽しめます。山開きは例年4～5月下旬ですが、新緑を楽しむなら6月がおすすめです。", image: "https://cdn.pixabay.com/photo/2019/09/14/03/31/landscape-4475213__480.jpg", region: "東北", address: "白神山地", status: nil, latitude: 40.4591, longitude: 140.164},
  {name: "平泉－仏国土（浄土）を表す建築・庭園及び考古学的遺跡群－（岩手県）", content: "平安時代末期、奥州藤原氏の時代の寺院や遺跡群が多数残る岩手県平泉地域。金色堂で知られる中尊寺がトリップアドバイザーでも平泉町の人気観光スポット1位になっているほか、毛越寺、観自在王院跡、無量光院跡、金鶏山といった5史跡から世界遺産が構成されています。それらの史跡とともに、奥州藤原氏の仏国土（浄土）思想もまた世界遺産登録の評価につながっているそうです。", image: "http://www.uraken.net/sozai/03iwate/photo/0021.jpg", region: "東北", address: "中尊寺", status: nil, latitude: 39.0019, longitude: 141.102},
  {name: "小笠原諸島（東京都）", content: "大陸と一度も陸続きになったことがなく、東洋のガラパゴスと呼ばれる小笠原諸島は、東京から南へ約1000kmの洋上にある海洋島です。大陸形成の歴史を紐解くカギが残されているとされ、地質学的にも注目されていますが、独自の進化を遂げた動植物も貴重な存在。30余りある島々の中でも、人気が高いのが南島。ただし入島には人数や時期の制限があるほか、入島にあたっては環境保護のための厳しいルールがあります。", image: "https://box.c.yimg.jp/res/box-l-3u7fgeo3gt4vpd3mddfvq2ss4a-1001?uid=cd0b8ba0-439d-48e4-9647-203e96a427cd&etag=77bbbec86ee0450f238849b13739d651", region: "関東", address: "南島", status: nil, latitude: 27.0383, longitude: 142.176},
  {name: "日光の社寺（栃木県）", content: "栃木県日光市は関東一の霊場として古くから知られ、多くの社寺が集まる場所。103棟もの建造物群を含む、日光東照宮、日光二荒山神社、日光山輪王寺の二社一寺が世界遺産に登録されています。なかでも徳川家康公の霊廟として創建された東照宮は、江戸時代の最高技術を用いた彫刻などの建築装飾が見事。外国人観光客も多く、トリップアドバイザーにも各言語の口コミが投稿されています。\n", image: "https://cdn.pixabay.com/photo/2017/06/07/04/03/nikko-2379222_1280.jpg", region: "関東", address: "日光東照宮", status: nil, latitude: 36.758, longitude: 139.599},
  {name: "富岡製糸場と絹産業遺産群（群馬県）", content: "日本の近代化への寄与と絹産業の国際的な技術交流や技術革新を果たしたとして、2014年に世界遺産に登録された富岡製糸場とその遺産群。具体的には富岡製糸場のほか養蚕業者の田島弥平旧宅、養蚕業の研究・教育機関であった高山社跡、蚕を貯蔵した荒船風穴などから構成されます。レンガ造りの美しいたたずまいを見せる富岡製糸場で、世界に評価された日本の絹産業の歴史に思いを馳せてみてください。", image: "http://www.tomioka-silk.jp/files/photo/14.jpg", region: "関東", address: "富岡製糸場", status: nil, latitude: 36.2568, longitude: 138.888},
  {name: "ル・コルビュジエの建築作品－近代建築運動への顕著な貢献－（東京都）", content: "2016年に登録された世界遺産が、フランスを拠点に活躍した近代建築の巨匠、ル・コルビュジエが手掛けた建築作品群です。非常に珍しいのが、7か国に残る17の建築群が構成資産になっており、大陸をまたぐ初の世界遺産になっている点。日本では東京・上野の国立西洋美術館が構成資産のひとつとなっています。日本で唯一、さらには東アジアで唯一のル・コルビュジエ建築とのことです。", image: "http://blog-imgs-51.fc2.com/s/a/b/sabo34/blog-UK2-1s.jpg", region: "関東", address: "国立西洋美術館", status: nil, latitude: 35.7156, longitude: 139.776},
  {name: "富士山－信仰の対象と芸術の源泉－（静岡県・山梨県）", content: "古くからある富士山信仰の存在や、万葉集の時代から文学や絵画に描かれてきた日本人の芸術の源泉として、「日本一の山」という事実を超えて、大きな意味合いを持つ富士山。登録エリアは山頂の信仰遺跡群から富士五湖、三保松原まで広範囲にわたります。当初自然遺産での登録を目指しましたが、ゴミ問題や開発などから文化遺産での登録となったことでも話題になりました。", image: "https://cdn.pixabay.com/photo/2016/10/01/06/13/japan-1706942__480.jpg", region: "中部", address: "富士山", status: nil, latitude: 35.3671, longitude: 138.725},
  {name: "白川郷・五箇山の合掌造り集落（岐阜県・富山県）", content: "日本の原風景がそのまま広がっているような白川郷・五箇山集落。合掌造りと呼ばれる切妻構造のかやぶき屋根の家屋が特徴的です。一般には岐阜県にある白川郷がよく知られていますが（写真）、規模の小さい富山県の五箇山は観光客の姿も少なく、のんびりとした雰囲気。より伝統的な集落の姿を見るなら、五箇山のほうがおすすめかもしれません。", image: "https://cdn.pixabay.com/photo/2015/04/16/15/22/japan-725802__480.jpg", region: "中部", address: "白川郷", status: nil, latitude: 36.2573, longitude: 136.906},
  {name: "古都京都の文化財（京都府・滋賀県）", content: "1994年に登録された世界遺産で、京都市、宇治市、大津市に点在する17の寺社や城郭で構成されています。トリップアドバイザーの京都市の人気観光名所ランキングでは2位に金閣寺、3位に清水寺と上位に軒並み世界遺産の寺社がランクインされています。雪に見舞われた日は、雪化粧された金閣寺をひと目見ようと、多くの観光客が訪れるそうです。", image: "https://cdn.pixabay.com/photo/2015/10/28/04/46/golden-pavilion-temple-1009991__480.jpg", region: "近畿", address: "金閣寺", status: nil, latitude: 35.0395, longitude: 135.729},
  {name: "古都奈良の文化財（奈良県）", content: "古都奈良にある8つの寺社を含む文化財が世界遺産に登録されています。8世紀に奈良に置かれた都、平城京はシルクロードの終着点とも言われ、交易の様子を伝える宝物が多数残されています。8つの登録施設は、京都や大阪からのアクセスも便利な奈良駅を中心にコンパクトにまとまっており、日帰りでの観光も可能。特に人気の東大寺は、有名な奈良の大仏や世界最大の木造建築である大仏殿など見どころがたくさんあります。", image: "https://cdn.pixabay.com/photo/2017/08/23/11/46/todai-ji-temple-2672649__480.jpg", region: "近畿", address: "東大寺", status: nil, latitude: 34.6891, longitude: 135.84},
  {name: "法隆寺地域の仏教建造物（奈良県）", content: "400年の歴史があり、現存する世界最古の木造建築として知られる法隆寺。1993年に日本で初めて世界遺産に登録され、世界的に注目を集めています。法隆寺は、東大寺や春日大社など名所が集中する奈良公園周辺エリアからは離れており、最寄りのJR法隆寺駅からも徒歩20分以上（バスで10分弱）とアクセスはやや不便。しかし、アクセスの悪さを上回るほどの感動を与えてくれることは、訪問者の評価の高さからも察することができます。", image: "https://cdn.pixabay.com/photo/2017/07/02/08/28/temple-2463674__480.jpg", region: "近畿", address: "法隆寺", status: nil, latitude: 34.6147, longitude: 135.736},
  {name: "紀伊山地の霊場と参詣道（和歌山県・奈良県・三重県）", content: "和歌山、奈良、三重の3県にまたがる紀伊山地は、古くから自然崇拝に根ざした清仏の霊場として知られています。世界遺産に登録されたのは、「吉野・大峯」「熊野三山」「高野山」と呼ばれる霊場とそこへ至る参詣道。金峯山寺（奈良県）、熊野本宮大社（和歌山県）、高野山奥之院（和歌山県）など、魂が揺さぶられるスポットが点在します。写真は熊野参詣道（熊野古道）。", image: "https://cdn.pixabay.com/photo/2017/06/10/06/43/kumano-ancient-road-2389157__480.jpg", region: "近畿", address: "熊野本宮大社", status: nil, latitude: 33.8409, longitude: 135.774},
  {name: "百舌鳥・古市古墳群（大阪府）", content: "4世紀後半から5世紀後半にかけての古墳時代に築造された、古代日本の王たちの墳墓群。大阪平野に集中しているのは、ここが当時の政治文化の中心地であり、大陸への航路の起点であったためと考えられています。墳丘の長さ約486mの大山古墳（仁徳天皇陵古墳）をはじめ、49基の古墳が登録されていて、空から軽飛行機で見渡すこともできます。", image: "https://box.c.yimg.jp/res/box-l-3u7fgeo3gt4vpd3mddfvq2ss4a-1001?uid=3ef2ed96-09ab-4ab1-bbbe-bdeab2448756&etag=36496313fbac88ced22eae999ff29b85", region: "近畿", address: "大山古墳", status: nil, latitude: 34.5652, longitude: 135.488},
  {name: "姫路城（兵庫県）", content: "1993年、法隆寺とともに日本初の世界遺産に登録された姫路城。その端正なたたずまいに多くのファンがいます。総工事期間5年半に及んだ「平成の大修理」を経て、2015年3月に美しくよみがえった姫路城は、まさに天下の名城。混雑必至ですが、桜の季節は美しい城と桜のコラボレーションが絶景をつくり上げます。", image: "https://cdn.pixabay.com/photo/2019/02/23/02/27/himeji-castle-4014850__480.jpg", region: "近畿", address: "姫路城", status: nil, latitude: 34.8396, longitude: 134.694},
  {name: "石見銀山遺跡とその文化的景観（島根県）", content: "島根県にある石見銀山は、1923年に休山するまで約400年にわたって良質な銀が採掘されてきた銀鉱山です。大航海時代の16世紀からヨーロッパでも広く知られた存在で、17世紀前半の全盛期には世界の産銀量の約3分の1を占めた日本銀のかなりの部分が石見銀山で産出されたものだったといわれています。休山によって、古い伝統的な銀産業の全体像を今に残しているのがポイント。情緒ある鉱山町の風情も見どころです。", image: "https://box.c.yimg.jp/res/box-l-3u7fgeo3gt4vpd3mddfvq2ss4a-1001?uid=ad8b6bcb-8ae6-4e9e-a0b7-69537b33071b&etag=524fcd9e326ab99bb4cfbbab04d71aad", region: "中国", address: "石見銀山", status: nil, latitude: 35.1054, longitude: 132.438},
  {name: "原爆ドーム（広島県）", content: "2016年にオバマ前大統領が訪問したことでも、世界的に注目された原爆ドーム。広島に投下された原子爆弾の惨禍を今に伝える建造物で、「二度と同じ悲劇が起こらないように」という祈りや戒めを込めて日本では負の世界遺産とも呼ばれています。トリップアドバイザーの広島市の人気観光地ランキングでは、広島平和記念資料館に次ぐ2位にランクインされており、世界的な関心の高さをうかがえます。", image: "https://cdn.pixabay.com/photo/2015/03/25/14/33/japan-689056__480.jpg", region: "中国", address: "原爆ドーム", status: nil, latitude: 34.3957, longitude: 132.454},
  {name: "厳島神社（広島県）", content: "古くから島そのものが信仰の対象とされていた広島県の宮島。瀬戸内海に浮かぶこの小さな島にある厳島神社は、日本のみならず世界中から観光客が集まる人気観光地です。寝殿造りの技を極めた雅な社殿群もさることながら、特に印象的なのが海上に建てられた高さ16mの大鳥居。1400年の歴史を感じるパワースポットでもあります。", image: "https://cdn.pixabay.com/photo/2015/04/16/15/22/hiroshima-725801__480.jpg", region: "中国", address: "厳島神社", status: nil, latitude: 34.2963, longitude: 132.32},
  {name: "明治日本の産業革命遺産 製鉄・製鋼、造船、石炭産業（山口県・鹿児島県・静岡県・岩手県・佐賀県・長崎県・福岡県・熊本県）", content: "幕末から明治時代に急速な発展を遂げ、日本の近代化に貢献した重工業に関する産業遺産。8県に点在する23の資産から構成され、そのうち8資産が現在も稼働中という珍しい世界遺産です。この世界遺産を象徴する場所として人気があるのが軍艦島の名で知られる長崎県の端島炭鉱。最盛期には8万3600人／1㎢と世界一の人口密度を誇り、日本初の鉄筋コンクリート造の集合住宅も造られました。閉山後は一気に無人化し、まさに廃墟。現在は、その稀有な姿をガイド付きで楽しむ観光ツアーが人気です。", image: "https://cdn.pixabay.com/photo/2016/10/08/09/29/japan-1723322__480.jpg", region: "九州", address: "端島炭鉱", status: nil, latitude: 32.6267, longitude: 129.738},
  {name: "長崎と天草地方の潜伏キリシタン関連遺産（長崎県、熊本県）", content: "17〜19世紀のキリスト教禁教政策の下で、ひそかに信仰を続けた人々の歴史を伝える遺産群。長崎県西部に五島列島、熊本県天草と、2県6市2町にまたがる12の資産で構成されています。長崎港を見下ろす高台に立つ大浦天主堂は、開国後に来日した宣教師が、200年以上も信仰を守った潜伏キリシタンと出会った歴史的な場所。天主堂内部には美しいリブ・ヴォールト天井が施され、日本最初期の洋風建築として国宝に指定されています。", image: "https://box.c.yimg.jp/res/box-l-3u7fgeo3gt4vpd3mddfvq2ss4a-1001?uid=56c939eb-a6a5-42aa-8065-9279ea133be2&etag=10729ded9cdc152a5138abb8fb73faec", region: "九州", address: "大浦天主堂", status: nil, latitude: 32.7343, longitude: 129.87},
  {name: "「神宿る島」宗像・沖ノ島と関連遺産群（福岡県）", content: "日本と朝鮮半島を結ぶ玄界灘に浮かび、宗像大社の沖津宮が鎮座する沖ノ島。古代から「神宿る島」として航海安全の祈りが捧げられ、4〜9世紀の祭祀の変遷を伝える遺跡を残しています。沖ノ島は神聖な場所のため旅行客は上陸できませんが、宗像市に辺津宮、フェリーで約25分の大島に中津宮が立ち、三宮にて宗像三女神を祀っています。大島北岸の沖津宮遥拝所からは、沖ノ島を御神体として拝むことができます（写真は沖ノ島に最も近づける場所である宗像大社沖津宮遙拝所）。", image: "https://box.c.yimg.jp/res/box-l-3u7fgeo3gt4vpd3mddfvq2ss4a-1001?uid=587cb379-4a20-4dfb-a08f-373f37a9f2fc&etag=fed22beb62cddf23eb2f196469cab531", region: "九州", address: "沖ノ島", status: nil, latitude: 34.2468, longitude: 130.106},
  {name: "屋久島（鹿児島県）", content: "白神山地とともに日本で初めて自然遺産に登録された屋久島。樹齢数千年の屋久杉をはじめ、多くの固有種を含む豊かな生物相を有し、さらに特異な生態系と美しい自然景観を持っている点が評価されました。片道約5～6時間かけてたどり着く縄文杉は、畏怖の念すら感じさせます。正確には、世界遺産に登録されたのは、島の西側の海岸から中央の山間部にかけての約20％で、映画『もののけ姫』の世界といわれる白谷雲水峡は、実は世界遺産エリアにはありません。", image: "https://cdn.pixabay.com/photo/2017/05/23/10/05/yakushima-island-2336735__480.jpg", region: "九州", address: "屋久島", status: nil, latitude: 30.3666, longitude: 130.506},
  {name: "琉球王国のグスク及び関連遺産群（沖縄県）", content: "15世紀の琉球王国時代に築かれた城（グスク）を中心とした遺跡群。琉球王国では中国、朝鮮、日本、東南アジア諸国との交易を基盤とした、国際色豊かな独自の文化が形成されました。そんな王国の繁栄を今に伝えるのが那覇市にある首里城。中国と日本の築城技術が融合した独特の建築は一見の価値があります。琉球舞踊の観覧や三線体験など、さまざまなイベントが用意されている店も、観光客に人気の秘密。", image: "http://sozai-free.com/sozai/images/219-syuri.jpg", region: "九州", address: "首里城", status: nil, latitude: 26.2172, longitude: 127.719}
])